mapper.select:
select * from board
	where


			bo_title like concat('%', #{cri.search}, '%') or
			bo_me_id = #{cri.search}


			bo_title like concat('%', #{cri.search}, '%')


			bo_me_id = #{cri.search}


	group by bo_num
	order by bo_num desc
	limit #{cri.pageStart}, #{cri.perPageNum}

select count(*) from board
where


		bo_title like concat('%', #{cri.search}, '%') or
		bo_me_id = #{cri.search}


		bo_title like concat('%', #{cri.search}, '%')


		bo_me_id = #{cri.search}
